use brewmap;
CREATE TABLE `store`
(
    `store_name` VARCHAR(50) primary key NOT NULL COMMENT '상호',
    `store_category` VARCHAR(20) NOT NULL COMMENT '술집분류',
    `store_tel` VARCHAR(20) NOT NULL COMMENT '전화번호',
    `store_addr` VARCHAR(50) NOT NULL COMMENT '주소',
    `store_regdate` DATETIME default NOW() COMMENT '술집등록일',
    `store_tag` VARCHAR(100) COMMENT '술집태그'
);


